const textoOriginal = $json.text_pdf || $json.text || "";

const textoLimpo = textoOriginal
  // Remove quebras de linha múltiplas e excessivas
  .replace(/[\n\r]+/g, " ")
  // Remove espaços repetidos
  .replace(/\s{2,}/g, " ")
  // Remove rodapé automático do TCPDF
  .replace(/Powered by TCPDF.*?$/, "")
  .replace(/\\?1 \/ 1/g, "")
  .trim();

return [{ json: { texto_limpo: textoLimpo } }];

