const raw = items[0].json.propertyName;

// Tenta remover aspas duplas externas inválidas no início/fim
let cleaned = raw.trim().replace(/^"+|"+$/g, '');

// Corrige campo "logs" malformado: tira aspas duplicadas externas da string do array
cleaned = cleaned.replace(/"logs":"\"/, '"logs":');
cleaned = cleaned.replace(/}\"\s*}$/, '}]}');

// Agora tentamos parsear
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (error) {
  throw new Error('Erro ao converter para JSON. Verifique a entrada. Detalhes: ' + error.message);
}

// Se ainda estiver com logs como string, tentamos parsear também
if (typeof parsed.logs === "string") {
  try {
    parsed.logs = JSON.parse(parsed.logs);
  } catch (e) {
    parsed.logs = [];
  }
}

return [{ json: parsed }];

const raw = items[0].json.propertyName;

// Tenta remover aspas duplas externas do JSON principal
let cleaned = raw.trim().replace(/^"+|"+$/g, '');

// Corrige o campo logs com regex
cleaned = cleaned
  .replace(/"logs":"\\*"?\[{/, '"logs":[{')   // início do array JSON
  .replace(/\\*"?}("?)}$/, '}]}');           // fim do array JSON

let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (error) {
  throw new Error('Erro ao converter para JSON. Verifique a entrada. Detalhes: ' + error.message + "\nJSON final:\n" + cleaned);
}

return [{ json: parsed }];
