const outputItems = [];

// Fun√ß√£o para limpar texto de poss√≠veis formata√ß√µes markdown
function cleanJsonText(text) {
  // Remove blocos de c√≥digo markdown (```json, ```jsonl, ou ``` ... ```)
  let cleaned = text.replace(/```(?:json|jsonl)?\s*\n?([\s\S]*?)\n?```/g, '$1');

  // Remove outras poss√≠veis formata√ß√µes
  cleaned = cleaned.replace(/^\s*```\s*$/gm, ''); // Remove linhas com apenas ```

  return cleaned.trim();
}

// Fun√ß√£o para validar se uma string √© um JSON v√°lido
function isValidJson(str) {
  try {
    JSON.parse(str);
    return true;
  } catch {
    return false;
  }
}

// Fun√ß√£o para tentar reparar JSON malformado
function attemptJsonRepair(jsonStr) {
  const repairs = [
    // Estrat√©gia 1: JSON original
    () => jsonStr,

    // Estrat√©gia 2: Escapar quebras de linha e aspas dentro de strings
    () => {
      return jsonStr.replace(/"([^"]*(?:\\.[^"]*)*)"/g, (match, content) => {
        // Se √© uma chave (seguida por :), n√£o modifica
        const afterMatch = jsonStr.slice(jsonStr.indexOf(match) + match.length).trim();
        if (afterMatch.startsWith(':')) {
          return match;
        }

        // √â um valor, escapa quebras de linha e aspas
        const escaped = content
          .replace(/\\/g, '\\\\')  // Escapa barras invertidas
          .replace(/"/g, '\\"')    // Escapa aspas
          .replace(/\n/g, '\\n')   // Escapa quebras de linha
          .replace(/\r/g, '\\r')   // Escapa retorno de carro
          .replace(/\t/g, '\\t');  // Escapa tabs

        return `"${escaped}"`;
      });
    }
  ];

  for (let i = 0; i < repairs.length; i++) {
    try {
      const repairedJson = repairs[i]();
      const parsed = JSON.parse(repairedJson);
      return { success: true, data: parsed, strategy: i + 1 };
    } catch (error) {
      continue;
    }
  }

  return { success: false };
}

// Fun√ß√£o para extrair objetos JSON de um texto
function extractJsonObjects(text) {
  const objects = [];
  let braceCount = 0;
  let currentObject = '';
  let inString = false;
  let escapeNext = false;

  for (let i = 0; i < text.length; i++) {
    const char = text[i];

    if (escapeNext) {
      currentObject += char;
      escapeNext = false;
      continue;
    }

    if (char === '\\' && inString) {
      currentObject += char;
      escapeNext = true;
      continue;
    }

    if (char === '"' && !escapeNext) {
      inString = !inString;
      currentObject += char;
      continue;
    }

    if (!inString) {
      if (char === '{') {
        if (braceCount === 0) {
          currentObject = char; // Inicia novo objeto
        } else {
          currentObject += char;
        }
        braceCount++;
      } else if (char === '}') {
        currentObject += char;
        braceCount--;

        if (braceCount === 0) {
          // Objeto completo encontrado
          const trimmed = currentObject.trim();
          if (trimmed) {
            objects.push(trimmed);
          }
          currentObject = '';
        }
      } else if (braceCount > 0) {
        currentObject += char;
      }
    } else {
      currentObject += char;
    }
  }

  return objects;
}

// Fun√ß√£o para processar objetos JSON extra√≠dos
function processJsonObjects(jsonObjects) {
  const results = [];

  for (let i = 0; i < jsonObjects.length; i++) {
    const jsonStr = jsonObjects[i];

    // Tenta parsing direto primeiro
    if (isValidJson(jsonStr)) {
      try {
        const parsed = JSON.parse(jsonStr);
        results.push({ json: parsed });
        continue;
      } catch (error) {
        // N√£o deveria chegar aqui se isValidJson retornou true
      }
    }

    // Tenta reparar o JSON
    const repairResult = attemptJsonRepair(jsonStr);

    if (repairResult.success) {
      results.push({ 
        json: {
          ...repairResult.data,
          _metadata: {
            repaired: true,
            strategy: repairResult.strategy,
            objectIndex: i + 1
          }
        }
      });
    } else {
      console.error(`Erro ao processar objeto JSON ${i + 1}:`, jsonStr);
      results.push({
        json: {
          error: "Erro de parse JSON",
          originalObject: jsonStr,
          objectIndex: i + 1,
          errorMessage: "Falha em todas as estrat√©gias de reparo"
        }
      });
    }
  }

  return results;
}

// Fun√ß√£o principal
function parseJsonlText(jsonlString) {
  if (typeof jsonlString !== 'string' || jsonlString.trim() === '') {
    console.warn("Entrada 'text' est√° vazia ou n√£o √© uma string v√°lida.");
    return [];
  }

  // Limpa poss√≠veis formata√ß√µes markdown
  const cleanedString = cleanJsonText(jsonlString);
  console.log("Texto limpo:", cleanedString.substring(0, 200) + "...");

  // Extrai objetos JSON do texto
  const jsonObjects = extractJsonObjects(cleanedString);
  console.log(`${jsonObjects.length} objeto(s) JSON encontrado(s)`);

  if (jsonObjects.length === 0) {
    console.warn("Nenhum objeto JSON encontrado no texto");
    return [];
  }

  // Processa os objetos encontrados
  const results = processJsonObjects(jsonObjects);
  console.log(`${results.length} objeto(s) processado(s) com sucesso`);

  return results;
}

