const outputItems = [];
const consolidatedStrings = [];

// Itera sobre CADA item que chega como input neste n√≥ Code (cada um sendo um objeto JSON)
for (const item of items) {
  const data = item.json; // Acessa o objeto JSON de cada item

  let itemStringParts = []; // Array tempor√°rio para construir a string de cada item

  // Itera sobre TODAS as chaves (propriedades) do objeto 'data'
  for (const key in data) {
    if (Object.prototype.hasOwnProperty.call(data, key)) { // Garante que √© uma propriedade pr√≥pria do objeto
      const value = data[key];

      // Adiciona a chave e o valor √† string, formatando como "Chave: Valor"
      // Voc√™ pode ajustar a formata√ß√£o aqui (ex: sem a chave, ou s√≥ o valor, etc.)
      itemStringParts.push(`${key}: ${value}`);
    }
  }

  // Junta todas as partes formatadas de um √∫nico item com quebra de linha
  // e adiciona ao array de strings consolidadas
  if (itemStringParts.length > 0) {
    consolidatedStrings.push(itemStringParts.join('\n'));
  }
}

// Junta todas as strings de itens consolidados com duas quebras de linha para separar os blocos de informa√ß√£o
const finalOutputString = consolidatedStrings.join('\n\n'); 

// Retorna um √∫nico item com a string organizada e consolidada
outputItems.push({
  json: {
    consolidatedText: finalOutputString
  }
});

return outputItems;

